    //composant Clock

    class Clock extends React.Component {
        constructor(props){
            super(props); // appel constructeur parent
            this.state = {date: new Date()};
        }

        componentDidMount(){ // dom ready
            this.timer = setInterval(() => {
                this.setState({date: new Date()});
            }, 1000);
        }

        componentWillUnmount() { //disparait du dom
            clearInterval(this.name);
        }

        render() {
            return <h2>
                Il est {this.state.date.toLocaleTimeString('fr-FR', {timeZone: this.props.timeZone})} √† {this.props.timeZone}
            </h2>;
        }
    }

    //compteur
    class MyCounter extends React.Component {
        constructor(props){
            super(props);
            this.state = {number: 0};
        }

        componentDidMount() {
            this.timer = setInterval(() => {
                //this.state.number++; fonctionne pas
                //this.state({number: this.state.number +1})
                this.setState((state) => ({
                    number: ++state.number
                }));
            }, 1000);
        }

        componentWillUnmount() {
            clearInterval(this.timer);
        }

        render() {
            return 'Compteur: ' +  this.state.number;
        }
    }

    //Les evenements
    class Button extends React.Component {
        constructor(props){
            super(props);
            this.state = {isToggle: true};

            this.handleClick = this.handleClick.bind(this);
        }

        handleClick(event) {
            console.log(event);
            this.setState(state => ({
                isToggle: !state.isToggle
            }));
        }
        render(){
            return (
                <button onClick={this.handleClick}>
                    {this.state.isToggle ? 'on' : 'off'}
                </button>
            );
        }
    }

    // TP compteur
    class Counter extends React.Component {
        constructor(props) {
            super(props);
            // On peut initialiser le compteur √† 4 par exemple
            this.state = {value: props.init ? props.init : 0};
        }
    
        handleIncrement() {
            this.setState(state => ({
                value: ++state.value
            }));
        }
    
        handleDecrement() {
            this.setState(state => ({
                value: --state.value
            }));
        }
    
        // increment vaut 1 ou -1
        /* handleChange(increment) {
            this.setState(state => ({
                value: state.value + increment
            }));
        } */
    
        render() {
            let buttonPlus = <button onClick={() => this.handleIncrement()}>+</button>;
    
            // Quand le max du compteur est atteint
            if (this.state.value >= this.props.max) {
                buttonPlus = null;
            }
    
            return (
                <div>
                    <button onClick={() => this.handleDecrement()}>-</button>
                    <span>{this.state.value}</span>
                    {buttonPlus}
                </div>
            );
        }
    }


